import React from "react";
import {Route, Switch} from 'react-router-dom'
import {MainPage} from "pages/main-page/MainPage";
import {UniversePage} from "pages/universe-page/UniversePage";
import {UserSettingsPage} from "pages/user-settings-page/UserSettingsPage";
import {GraphsPage} from "pages/graphs-page/GraphsPage";
import FilmsContainer from "components/films/FilmsContainer";
import PlanetsContainer from "components/planets/PlanetsContainer";
import StarshipsContainer from "components/starships/StarshipsContainer";
import CharactersContainer from "components/characters/CharactersContainer";

export const MainRouter = () => {
    return (
        <>
            <Switch>
                <Route path='/' exact render={() => <MainPage/>}/>
                <Route path='/catalog' exact render={() => <UniversePage/>}/>
                <Route path='/graphs' render={() => <GraphsPage/>}/>
                <Route path='/account' render={() => <UserSettingsPage/>}/>
                <Route path='/catalog/characters' render={()=><CharactersContainer/>}/>
                <Route path='/catalog/films' render={() => <FilmsContainer/>}/>
                <Route path='/catalog/planets' render={() => <PlanetsContainer/>}/>
                <Route path='/catalog/starships' render={() => <StarshipsContainer/>}/>
            </Switch>
        </>
    )
}