import React from "react";
import s from './characters.module.scss'
import {Carousel, Button} from "react-bootstrap";
import cn from 'classnames';
import arrData from "components/mockesData";
import {NavLink} from "react-router-dom";

export const Characters = () => {

    return (
        <div className={s.mainWrapper}>
            <Carousel>
                {arrData.map(d =>
                    <Carousel.Item>
                        <div className={s.mainContent}>
                            <aside className={cn(s.characterInfo, s.left)}>
                                <p><span>name:</span> {d.name}</p>
                                <p><span>gender:</span> {d.gender}</p>
                                <p><span>birth year:</span> {d.birth_year}</p>
                                <p><span>height:</span> {d.height}</p>
                                <p><span>mass:</span> {d.mass}</p>
                                <p><span>eye color:</span> {d.eye_color}</p>
                            </aside>
                            <img
                                className={s.image}
                                src={`https://starwars-visualguide.com/assets/img/characters/${arrData.indexOf(d) + 1}.jpg`}
                            />
                            <aside className={cn(s.characterInfo, s.right)}>
                                <p><span>home planet:</span> <NavLink to='/'>Planet № {d.homeworld.slice(-2, -1)}</NavLink></p>
                                <div className={s.films}>
                                    <h4>Was in the:</h4>
                                    {d.films.map(f => <NavLink to='/'>Movie № {f.slice(-2, -1)}</NavLink>)}
                                </div>
                            </aside>
                        </div>
                        <Carousel.Caption>
                            <h3>{d.name}</h3>
                            <Button
                                variant="outline-secondary"
                                size='sm'
                                >Add to favorite</Button>
                        </Carousel.Caption>
                    </Carousel.Item>
                )}


            </Carousel>
        </div>
    )
}