import {
    charactersActionTypes,
    FETCH_ALL_CHARACTERS_FAILURE,
    FETCH_ALL_CHARACTERS_REQUEST,
    FETCH_ALL_CHARACTERS_SUCCESS,
    PUT_ALL_CHARACTERS
} from "components/characters/charactersTypes";


export const allCharactersRequested = ():charactersActionTypes => {
    return {
        type: FETCH_ALL_CHARACTERS_REQUEST
    }
}
export const allCharactersSuccess = ():charactersActionTypes => {
    return {
        type: FETCH_ALL_CHARACTERS_SUCCESS
    }
}
export const allCharactersFailure = ():charactersActionTypes => {
    return {
        type: FETCH_ALL_CHARACTERS_FAILURE
    }
}

export const putAllCharacters = (payload: any):charactersActionTypes => {
    return {
        type: PUT_ALL_CHARACTERS,
        payload
    }
}