import {takeEvery, all ,call} from 'redux-saga/effects'
import {FETCH_ALL_CHARACTERS_REQUEST} from "components/characters/charactersTypes";
import {fetchAllCharactersWorker} from "components/characters/saga/workers/fetchAllCharactersWorker";



function* getAllCharacters() {
    yield takeEvery(FETCH_ALL_CHARACTERS_REQUEST, fetchAllCharactersWorker)
}

export function* charactersSaga() {
    yield all([call(getAllCharacters)]);
}