import React from "react";
import {usePlanetsChart} from "components/charts/planets-comparison/hooks/usePlanetsChart";
import {Loader} from "components/loader/Loader";
import {Charts} from "components/charts/planets-comparison/chart/Charts";

export const PlanetsComparisonChart = () => {

    const {isLoading} = usePlanetsChart()

    return (
        <section>
            {isLoading
                ?
                <Loader/>
                :
                <Charts/>
            }
        </section>
    )
}