import React, {useEffect} from "react";
import {usePlanetsChart} from "components/charts/planets-population/hooks/usePlanetsChart";
import {Bar} from "react-chartjs-2";
import s from './planetsCharts.module.scss'

export const PlanetsComparisonChart = () => {

    const {planetsForChart, chart, chartData, chartOptions, getChartOptions} = usePlanetsChart()
/*    {planetsForChart.map( (p: { name: React.ReactNode; climate: React.ReactNode; population: React.ReactNode; }) => <div>
        <p>{p.name}</p>
        <p>{p.climate}</p>
        <p>{p.population}</p></div>)}*/

    useEffect(() => {
        chart()
    }, [])
    useEffect(() => {
        getChartOptions()
    }, [])

    return (
        <section>
            <div className={s.mainWrapper}>
               <Bar data={chartData} options={chartOptions} />
            </div>
        </section>
    )
}