import {
    FETCH_NEW_PLANET_PAGE_REQUEST,
    FETCH_NEW_PLANET_PAGE_SUCCESS,
    planetsPaginationType,
    SET_CURRENT_PAGE,
    SET_DATA_PLANETS_FOR_PAGINATION
} from "components/charts/planets-population/types";


export const setCurrentPage = (payload: number): planetsPaginationType => ({
    type: SET_CURRENT_PAGE,
    payload
})

export const fetchNewPlanetsPageRequest= (payload: number): planetsPaginationType => ({
    type: FETCH_NEW_PLANET_PAGE_REQUEST,
    payload
})

export const fetchNewPlanetsPageSuccess = (): planetsPaginationType => ({
    type: FETCH_NEW_PLANET_PAGE_SUCCESS
})

export const setPlanetsForPagination = (payload: any): planetsPaginationType => ({
    type: SET_DATA_PLANETS_FOR_PAGINATION,
    payload
})