
export const SET_CURRENT_PAGE = 'SET_CURRENT_PAGE'
type setCurrentPageType = {
    type: typeof SET_CURRENT_PAGE
    payload: number
}

export const FETCH_NEW_PLANET_PAGE_REQUEST = 'FETCH_NEW_PLANET_PAGE_REQUEST'
type fetchNewPageRequestType = {
    type: typeof FETCH_NEW_PLANET_PAGE_REQUEST
    payload: number
}

export const FETCH_NEW_PLANET_PAGE_SUCCESS = 'FETCH_NEW_PLANET_PAGE_SUCCESS'
type fetchNewPageSuccessType = {
    type: typeof FETCH_NEW_PLANET_PAGE_SUCCESS
}
export const SET_DATA_PLANETS_FOR_PAGINATION = 'SET_DATA_PLANETS_FOR_PAGINATION'
export type setPlanetsForPaginationType = {
    type: typeof SET_DATA_PLANETS_FOR_PAGINATION
    payload: any
}

export type planetsPaginationType =
    | setCurrentPageType
    | fetchNewPageSuccessType
    | setPlanetsForPaginationType
    | fetchNewPageRequestType