import React from "react";
import {Loader} from "components/loader/Loader";
import {Charts} from "components/charts/starships-comparison/chart/Charts";
import {useShipsChart} from "components/charts/starships-comparison/hooks/useShipsChart";

export const ShipsComparisonChart = () => {

    const {isLoading} = useShipsChart()

    return (
        <section>
            {isLoading
                ?
                <Loader/>
                :
                <Charts/>
            }
        </section>
    )
}