import {all, call, takeEvery} from 'redux-saga/effects'
import {SagaIterator} from "@redux-saga/types";
import {FETCH_NEW_SHIPS_PAGE_REQUEST} from "components/charts/starships-comparison/types";
import {shipsComparisonWorker} from "components/charts/starships-comparison/saga/workers/shipsComparisonWorker";


function* getPageOfShips(): SagaIterator{
    // @ts-ignore
    yield takeEvery(FETCH_NEW_SHIPS_PAGE_REQUEST, shipsComparisonWorker)
}






export function* shipsComparisonSaga(): SagaIterator {
    yield all([call(getPageOfShips)])
}