import {useDispatch, useSelector} from "react-redux";
import {AppState} from "init/rootReducer";
import {PlanetsPopulationStateType} from "components/charts/planets-population/reducer";
import {fetchNewPlanetsPageRequest} from "components/charts/planets-population/actions";
import {useEffect} from "react";
import {ShipsComparisonStateType} from "components/charts/starships-comparison/reducer";
import {fetchNewShipsPageRequest} from "components/charts/starships-comparison/actions";
import {YourPlanetStateType} from "components/charts/your-planet/reducer";
import {fetchNewYourPlanetPageRequest} from "components/charts/your-planet/actions";

export const useYourPlanet = () => {
    const dispatch = useDispatch()

    const {currentPage, charactersList, isLoading, countAllCharacters} = useSelector<AppState, YourPlanetStateType>(
        state => state.yourPlanetReducer
    )

    const getNextYourPlanetPage = (pageNumber: number) => {
        dispatch(fetchNewYourPlanetPageRequest(pageNumber))
    }

    return {
        currentPage,
        charactersList,
        isLoading,
        countAllCharacters,
        getNextYourPlanetPage
    }
}