import {all, call, takeEvery} from 'redux-saga/effects'
import {SagaIterator} from "@redux-saga/types";
import {
    FETCH_DATA_FOR_CHARACTERS_COMPARISON,
    FETCH_NEW_YOUR_PLANET_PAGE_REQUEST
} from "components/charts/your-planet/types";
import {yourPlanetWorker} from "components/charts/your-planet/saga/workers/yourPlanetWorker";
import {charactersForComparisonWorker} from "components/charts/your-planet/saga/workers/charactersForComparisonWorker";


function* getPageOfYourPlanet(): SagaIterator{
    // @ts-ignore
    yield takeEvery(FETCH_NEW_YOUR_PLANET_PAGE_REQUEST, yourPlanetWorker)
}


function* getCharactersForComparison(): SagaIterator{
    // @ts-ignore
    yield takeEvery(FETCH_DATA_FOR_CHARACTERS_COMPARISON, charactersForComparisonWorker)
}






export function* yourPlanetSaga(): SagaIterator {
    yield all([call(getPageOfYourPlanet), call(getCharactersForComparison)])
}