import React from "react";
import {Films} from "components/films/Films";
import {connect} from "react-redux";
import {allFilmsRequested} from "components/films/filmsActions";

const FilmsContainer = (props: any) => {
    return (
        <div>
                <Films filmsList={props.allFilms}
                       isLoading={props.isLoading}
                       getAllFilms={props.getAllFilms}
                />
        </div>
    )
}

let mapStateToProps = (state:any) => ({
        allFilms: state.filmsReducer.allFilms,
        isLoading: state.filmsReducer.isLoading,
    })


// @ts-ignore
let mapDispatchToProps = (dispatch) => ({
        getAllFilms: () => {
            dispatch(allFilmsRequested())
        },
    })

export default connect(mapStateToProps, mapDispatchToProps)(FilmsContainer);