import produce from 'immer'
import {PUT_ALL_FILMS} from "components/films/flmsActions";

const initialState = {
    allFilms: [],
    isLoading: false,
    favorite: []
}

export const filmsReducer = (state = initialState, action:any) => {
    produce(state, draft => {
        switch(action.type){
            case PUT_ALL_FILMS:
                draft.allFilms.push(action.payload);
                draft.isLoading = true;
                console.log(action.payload)
                break;

            default:
                break
        }
    })
}