import produce from 'immer';
import {PUT_ALL_FILMS} from "components/films/filmsTypes";

/*type initialStateType = {
    allFilms: Array<Object>,
    isLoading: boolean,
    isError: boolean,
    favorite: Array<string>
}*/

const initialState = {
    allFilms: [],
    isLoading: false,
    isError: false,
    favorite: []
}

export const filmsReducer = (state = initialState, action:any) =>
    produce(state, draft => {
        switch(action.type){
            case PUT_ALL_FILMS:
                draft.allFilms.push(action.payload);
                draft.isLoading = true;
                console.log(action.payload)
                break;
            default:
                break
        }
    })
