//for saga fetching
export const FETCH_ALL_FILMS_REQUEST = 'FETCH_ALL_FILMS_REQUEST';
export const FETCH_ALL_FILMS_SUCCESS = 'FETCH_ALL_FILMS_SUCCESS';
export const FETCH_ALL_FILMS_FAILURE= 'FETCH_ALL_FILMS_FAILURE';


export const allFilmsRequested = () => {
    return {
        type: FETCH_ALL_FILMS_REQUEST
    }
}
export const allFilmsSuccess = () => {
    return {
        type: FETCH_ALL_FILMS_SUCCESS
    }
}
export const allFilmsFailure = () => {
    return {
        type: FETCH_ALL_FILMS_FAILURE
    }
}


//for reducer
export const PUT_ALL_FILMS = 'PUT_ALL_FILMS';

export const putAllFilms = (payload) => {
    return {
        type: PUT_ALL_FILMS,
        payload
    }
}


