import React from "react";
import {Form, Button} from "react-bootstrap";
import './loginForm.scss'
import  {Field, reduxForm} from "redux-form";

const LoginForm = () => {

    return (
            <section className="mainWrapper">
                <div className="formWrapper">
                    <form id="signin" method="" action="" autoComplete="off">
                        <Field type="text" id="user" name="user" placeholder="username"/>
                        <Field type="password" id="pass" name="pass" placeholder="password"/>
                        <button type="submit">&#xf0da;</button>
                        <p>forgot your password? <a href="#">click here</a></p>
                    </form>
                    <img className="r2d2" src={require('assets/backgrounds/r2d2.png')} alt=""/>
                </div>
            </section>
    )
}

export const LoginReduxForm = reduxForm({form: 'signIn'})(LoginForm)