import {
    FETCH_ALL_PLANETS_FAILURE,
    FETCH_ALL_PLANETS_REQUEST,
    FETCH_ALL_PLANETS_SUCCESS,
    planetsActionTypes,
    PUT_ALL_PLANETS
} from "components/planets/planetsTypes";


export const allPlanetsRequested = ():planetsActionTypes => {
    return {
        type: FETCH_ALL_PLANETS_REQUEST
    }
}
export const allPlanetsSuccess = ():planetsActionTypes => {
    return {
        type: FETCH_ALL_PLANETS_SUCCESS
    }
}
export const allPlanetsFailure = ():planetsActionTypes => {
    return {
        type: FETCH_ALL_PLANETS_FAILURE
    }
}

export const putAllPlanets = (payload: any):planetsActionTypes => {
    return {
        type: PUT_ALL_PLANETS,
        payload
    }
}