export const FETCH_ALL_PLANETS_REQUEST = 'FETCH_ALL_PLANETS_REQUEST';
export const FETCH_ALL_PLANETS_SUCCESS = 'FETCH_ALL_PLANETS_SUCCESS';
export const FETCH_ALL_PLANETS_FAILURE= 'FETCH_ALL_PLANETS_FAILURE';

export const allPlanetsRequested = () => {
    return {
        type: FETCH_ALL_PLANETS_REQUEST
    }
}
export const allPlanetsSuccess = () => {
    return {
        type: FETCH_ALL_PLANETS_SUCCESS
    }
}
export const allPlanetsFailure = () => {
    return {
        type: FETCH_ALL_PLANETS_FAILURE
    }
}