import React from "react";
import s from 'components/quiz/quiz.module.scss'
import {Carousel} from "react-bootstrap";
import cn from "classnames";
import arrData from "components/mockesData";


export const Quiz = ({filmsProps}:any) => {



    return (
        <div className={s.mainWrapper}>
            <Carousel>
                {filmsProps.map( ((f: { name: {} | null | undefined; gender: {} | null | undefined; birth_year: {} | null | undefined; height: {} | null | undefined; eye_color: {} | null | undefined; })  =>
                    <Carousel.Item>
                        <div className={s.mainContent}>
                            <div className={cn(s.characterInfo, s.left)}>
                                <p><span>name:</span> {f.name}</p>
                                <p><span>gender:</span> {f.gender}</p>
                                <p><span>birth year:</span> {f.birth_year}</p>
                                <p><span>height year:</span> {f.height}</p>
                                <p><span>eye color:</span> {f.eye_color}</p>
                            </div>
                            <img
                                className={s.image}
                                src={`https://starwars-visualguide.com/assets/img/films/${filmsProps.indexOf(f) + 1}.jpg`}
                            />
                            <div className={cn(s.characterInfo, s.right)}>
                                <p><span>home planet:</span> {f.name}</p>
                                <p><span>gender:</span> {f.gender}</p>
                                <p><span>birth year:</span> {f.birth_year}</p>
                                <p><span>height year:</span> {f.height}</p>
                                <p><span>eye color:</span> {f.eye_color}</p></div>
                        </div>
                        <Carousel.Caption>
                            <h3>{f.name}</h3>
                            <p>{f.gender}</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                ))}

            </Carousel>
        </div>
    )
}