import React from "react";
import s from './quizEnd.module.scss'

const QuizEndItem = (props:any) => {
    return (
        <div className={s.mainWrapper}>
            {props}
        </div>
    )
}

export const QuizEnd = ({rightAnswers}: any) => {

    const first = <div><h2>Ваш уровень знаний - Имперский штурмовик!</h2>
        <p>Попробуйте вступить в 501 легион: там и
        паёк лучше
        да и перспектив больше</p></div>

    const second = <div><h2>Ваш уровень знаний - Император Палпатин!</h2>
        <p>Невежество питает темную сторону Силы.
        Светлая
        сторона Силы откроет многие новые знания вам</p></div>

    const third = <div><h2>Ваш уровень знаний - C3PO!</h2>
        <p>Знание правил этикета и всех языков Вселенной
        ещё не делают вас экспертом по "Звездным Войнам"</p></div>

    const forth = <div><h2>Джордж Лукас, разлогиньтесь!</h2>
        <p>Человечество вам признательно за всё,
        что вы сделали и придумали</p></div>


    if(rightAnswers < 5){
        //@ts-ignore
       return <QuizEndItem props={first}/>
    }

    if(rightAnswers >= 5 && rightAnswers <= 12){
        //@ts-ignore
        return <QuizEndItem props={second}/>
    }

    if(rightAnswers > 14 && rightAnswers <= 17){
        //@ts-ignore
        return <QuizEndItem props={third}/>
    }
    if(rightAnswers > 17){
        //@ts-ignore
        return <QuizEndItem props={forth}/>
    }
}