import React from "react";
import s from './questionContainer.module.scss'
import {Button} from "react-bootstrap";
import {useQuize} from "components/quiz/hooks/useQuize";


export const QuestionContainer = () => {
    const {question, questionCounter, rightAnswers, invokeNewQuestion} = useQuize()

    return (
        <div className={s.mainWrapper}>
            <div className={s.counter}>
                {`${questionCounter} из 19`}
            </div>

            <h2 className={s.header}>{question.question}</h2>

            <ul>
                <li className={s.answersItem}>{question.answer1?.text}</li>
                <li className={s.answersItem}>{question.answer2?.text}</li>
                <li className={s.answersItem}>{question.answer3?.text}</li>
                <li className={s.answersItem}>{question.answer4?.text}</li>
            </ul>
            <Button onClick={invokeNewQuestion}>Next question</Button>
        </div>
    )
}