import {
    PUT_NEW_QUESTION,
    quizActionTypes,
    SET_NEXT_QUESTION,
    SET_RIGHT_QUESTION,
    SET_WRONG_QUESTION
} from "components/quiz/quizTypes";


export const nextQuestion = (): quizActionTypes => {
    return {
        type: SET_NEXT_QUESTION
    }
}

export const rightAnswer = (): quizActionTypes => {
    return {
        type: SET_RIGHT_QUESTION
    }
}

export const wrongAnswer = (): quizActionTypes => {
    return {
        type: SET_WRONG_QUESTION
    }
}

export const putNewQuestion = (payload: any):quizActionTypes => {
    return {
        type: PUT_NEW_QUESTION,
        payload
    }
}

