import produce from 'immer';
import {PUT_NEW_QUESTION, quizActionTypes, SET_NEXT_QUESTION, SET_RIGHT_QUESTION} from "components/quiz/quizTypes";
import {rightAnswer} from "components/quiz/quizActions";


export type quizStateType = {
    questionCounter: number
    question: any
    rightAnswers: number
}

const initialState: quizStateType = {
    questionCounter: 0,
    question:{},
    rightAnswers: 0,
}

export const quizReducer = (state=initialState, action:quizActionTypes): quizStateType =>
    produce(state, draft => {
        switch(action.type){
            case SET_NEXT_QUESTION:
                draft.questionCounter++
                break;
            case SET_RIGHT_QUESTION:
                draft.rightAnswers++
                break;

            case PUT_NEW_QUESTION:
                draft.question = action.payload
                break;

            default:
                break
        }
    })
