
export const SET_NEXT_QUESTION = 'SET_NEXT_QUESTION';
type nextQuestionAction = {
    type: typeof SET_NEXT_QUESTION
}

export const SET_RIGHT_QUESTION = 'SET_RIGHT_QUESTION';
export type rightAnswerAction = {
    type: typeof SET_RIGHT_QUESTION,
}
export const SET_WRONG_QUESTION = 'SET_WRONG_QUESTION';
export type wrongAnswerAction = {
    type: typeof SET_WRONG_QUESTION,
}

export const PUT_NEW_QUESTION = 'PUT_NEW_QUESTION';
export type putNewQuestion = {
    type: typeof PUT_NEW_QUESTION,
    payload: any
}


export type quizActionTypes =
    | nextQuestionAction
    | rightAnswerAction
    | wrongAnswerAction
    | putNewQuestion
