import {all, call, takeEvery} from 'redux-saga/effects';
import {SagaIterator} from "@redux-saga/types";
import {nextQuestionWorker} from "components/quiz/saga/workers/quizWorker";
import {SET_NEXT_QUESTION} from "components/quiz/quizTypes";


function* getNextQuestion(): SagaIterator {
    yield takeEvery(SET_NEXT_QUESTION, nextQuestionWorker)
}



//local root saga for export
export function* quizSaga(): SagaIterator {
    yield all([call(getNextQuestion)]);
}