import {put} from 'redux-saga/effects';
// @ts-ignore
import {questionList} from "components/quiz/questions-for-quiz/questions";
import {putNewQuestion} from "components/quiz/quizActions";


export function* workerQ(i = 0) {
    while (true) {
        yield questionList[i++];
    }
}

let action = workerQ()

export function* nextQuestionWorker() {

    yield put(putNewQuestion(action.next().value))
}