import React from "react";
import {Starships} from "components/starships/Starships";
import {connect} from "react-redux";
import {allStarshipsRequested} from "components/starships/starshipsActions";

const StarshipsContainer = (props: any) => {
    return (
        <>
            <Starships starshipsList={props.allStarships}
                       isLoading={props.isLoading}
                       getAllStarships={props.getAllStarships}/>
        </>
    )
}

let mapStateToProps = (state: any) => ({
    allStarships: state.starshipsReducer.allStarships,
    isLoading: state.starshipsReducer.isLoading
})


// @ts-ignore
let mapDispatchToProps = (dispatch) => ({
    getAllStarships: () => {
        dispatch(allStarshipsRequested())
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(StarshipsContainer);