import {useDispatch} from 'react-redux';
import {allStarshipsRequested} from "components/starships/starshipsActions";


export const useStarshipsFetch = () => {
    const dispatch = useDispatch();


    const getAllStarships = () => {
        dispatch(allStarshipsRequested())
    }



    return {
        getAllStarships,
    };
};