import {takeEvery, all ,call} from 'redux-saga/effects'
import {fetchAllStarshipsWorker} from "components/starships/saga/workers/fetchAllStarshipsWorker";
import {FETCH_ALL_STARSHIPS_REQUEST} from "components/starships/starshipsTypes";



function* getAllStarships() {
    yield takeEvery(FETCH_ALL_STARSHIPS_REQUEST, fetchAllStarshipsWorker)
}

export function* starshipsSaga() {
    yield all([call(getAllStarships)]);
}