export const FETCH_ALL_STARSHIPS_REQUEST = 'FETCH_ALL_STARSHIPS_REQUEST';
export const FETCH_ALL_STARSHIPS_SUCCESS = 'FETCH_ALL_STARSHIPS_SUCCESS';
export const FETCH_ALL_STARSHIPS_FAILURE= 'FETCH_ALL_STARSHIPS_FAILURE';

export const allStarshipsRequested = () => {
    return {
        type: FETCH_ALL_STARSHIPS_REQUEST
    }
}
export const allStarshipsSuccess = () => {
    return {
        type: FETCH_ALL_STARSHIPS_SUCCESS
    }
}
export const allStarshipsFailure = () => {
    return {
        type: FETCH_ALL_STARSHIPS_FAILURE
    }
}