import {
    FETCH_ALL_STARSHIPS_FAILURE,
    FETCH_ALL_STARSHIPS_REQUEST,
    FETCH_ALL_STARSHIPS_SUCCESS,
    PUT_ALL_STARSHIPS,
    starshipsActionTypes
} from "components/starships/starshipsTypes";


export const allStarshipsRequested = ():starshipsActionTypes => {
    return {
        type: FETCH_ALL_STARSHIPS_REQUEST
    }
}
export const allStarshipsSuccess = ():starshipsActionTypes => {
    return {
        type: FETCH_ALL_STARSHIPS_SUCCESS
    }
}
export const allStarshipsFailure = ():starshipsActionTypes => {
    return {
        type: FETCH_ALL_STARSHIPS_FAILURE
    }
}

export const putAllStarships = (payload: any): starshipsActionTypes => {
    return {
        type: PUT_ALL_STARSHIPS,
        payload
    }
}
