import {combineReducers} from "redux";
import {characterReducer} from "init/reducers/charactersReducer";
import {filmsReducer} from "init/reducers/filmsReducer";
import {planetsReducer} from "init/reducers/planetsReducer";
import {starshipsReducer} from "init/reducers/starshipsReducer";

export const rootReducer = combineReducers({
    characterReducer,
    filmsReducer,
    planetsReducer,
    starshipsReducer
})

