import {all, fork} from 'redux-saga/effects';
import {filmsSaga} from 'components/films/saga/filmsSaga'
import {starshipsSaga} from "components/starships/saga/starshipsSaga";
import {planetsSaga} from "components/planets/saga/planetsSaga";
import {charactersSaga} from "components/characters/saga/charactersSaga";
import {quizSaga} from "components/quiz/saga/quizSaga";
import {planetsPopulationSaga} from "components/charts/planets-population/saga/planetsPopulationSaga";
import {shipsComparisonSaga} from "components/charts/starships-comparison/saga/shipsComparisonSaga";
import {yourPlanetSaga} from "components/charts/your-planet/saga/yourPlanetSaga";

export function* rootSaga() {
    yield all([
        fork(filmsSaga),
        fork(starshipsSaga),
        fork(planetsSaga),
        fork(charactersSaga),
        fork(quizSaga),
        fork(planetsPopulationSaga),
        fork(shipsComparisonSaga),
        fork(yourPlanetSaga)
    ])
}
