import React from "react";
import s from "./chartsPage.module.scss";
import {NavLink} from "react-router-dom";
import cn from "classnames";
import {usePlanetPopulation} from "components/charts/planets-comparison/hooks/usePlanetsPopulation";
import {useShipsComparison} from "components/charts/starships-comparison/hooks/useShipsComparison";
import {useYourPlanet} from "components/charts/your-planet/hooks/useYourPlanet";

export const ChartsPage = () => {

    const {getNextPage} = usePlanetPopulation()
    const {getNextShipsPage} = useShipsComparison()
    const {getNextYourPlanetPage} = useYourPlanet()

    return (
        <section className={s.mainWrapper}>

            <NavLink
                //@ts-ignore
                    onClick={() => getNextPage(1)}
                     className={cn(s.blockWrapper,s.characters)} to='/charts/population'>
                <div className={s.blockHeader}>Planets population</div>
            </NavLink>

            <NavLink
                    onClick={() => getNextShipsPage(1)}
                     className={cn(s.blockWrapper,s.planets)} to='/charts/starships'>
                <div className={s.blockHeader}>Compare starships</div>
            </NavLink>

            <NavLink
                    onClick={() => getNextYourPlanetPage(1)}
                    className={cn(s.blockWrapper,s.starships)} to='/charts/your-planet'>
                <div className={s.blockHeader}>Your planet</div>
            </NavLink>
        </section>
    )
}