import React from "react";
import s from "./quizPage.module.scss";
import {NavLink} from "react-router-dom";
import {Button} from "react-bootstrap";
import {useQuize} from "components/quiz/hooks/useQuize";

export const QuizPage = () => {
    const {invokeNewQuestion} = useQuize()
    return (
        <section className={s.mainWrapper}>
            <div className={s.description}>
                <h2>Right here you will try to test yourself.</h2>
                <br/>
                <p>What's about a little quiz?</p>
                <p>Answer the questions and will find out how big fan you are.</p>
            </div>

            <NavLink to='/quiz/game' className={s.startButton}>
                <Button
                    block
                    size="lg"
                    variant='outline-warning'
                    onClick={invokeNewQuestion}>
                    start the game
                </Button>
            </NavLink>
        </section>
    )
}