import React from "react";
import {NavLink} from "react-router-dom";
import s from './worldPage.module.scss';
import cn from 'classnames';
import {useSpring, animated} from "react-spring";

export const WorldPage = () => {
    const props = useSpring({
        from:{opacity: 0},
        to: {opacity: 1},
        config:{duration: 500}})
    return (

            <animated.section className={s.mainWrapper} style={props}>
                <NavLink className={cn(s.blockWrapper, s.characters)} to='/world/characters'>
                    <div className={s.blockHeader}>Characters</div>
                </NavLink>

                <NavLink className={cn(s.blockWrapper, s.planets)} to='/world/planets'>
                    <div className={s.blockHeader}>Planets</div>
                </NavLink>

                <NavLink className={cn(s.blockWrapper, s.starships)} to='/world/starships'>
                    <div className={s.blockHeader}>Starships</div>
                </NavLink>

                <NavLink className={cn(s.blockWrapper, s.films)} to='/world/films'>
                    <div className={s.blockHeader}>Films</div>
                </NavLink>
            </animated.section>



    )
}