import React from "react";
import {NavLink} from "react-router-dom";
import s from './worldPage.module.scss';
import cn from 'classnames';
import {useCharactersFetch} from "components/characters/hooks/useCharactersFetch";
import {usePlanetsFetch} from "components/planets/hooks/usePlanetsFetch";
import {useStarshipsFetch} from "components/starships/hooks/useStarshipsFetch";
import {useFilmsFetch} from "components/films/hooks/useFilmsFetch";

export const WorldPage = () => {
    const {getAllCharacters} = useCharactersFetch()
    const {getAllPlanets} = usePlanetsFetch()
    const {getAllStarships} = useStarshipsFetch()
    const {getAllFilms} = useFilmsFetch()

    return (

            <section className={s.mainWrapper} >
                <NavLink onClick={getAllCharacters} className={cn(s.blockWrapper, s.characters)} to='/world/characters'>
                    <div className={s.blockHeader}>Characters</div>
                </NavLink>

                <NavLink onClick={getAllPlanets} className={cn(s.blockWrapper, s.planets)} to='/world/planets'>
                    <div className={s.blockHeader}>Planets</div>
                </NavLink>

                <NavLink onClick={getAllStarships} className={cn(s.blockWrapper, s.starships)} to='/world/starships'>
                    <div className={s.blockHeader}>Starships</div>
                </NavLink>

                <NavLink onClick={getAllFilms} className={cn(s.blockWrapper, s.films)} to='/world/films'>
                    <div className={s.blockHeader}>Films</div>
                </NavLink>
            </section>



    )
}