import axios from "axios";

const instance = axios.create({
    baseURL: 'https://swapi.dev/api/',
});


export const charactersApi = {
    getCharactersList() {
        return instance.get(`people/`).then(response => response.data)
    },
    getCharacter(id:number) {
        return instance.get(`people/${id}/`).then(response => response.data)
    },
}

